import { makeStyles } from '@material-ui/core/styles';

export default makeStyles((theme) => ({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
        },
    },
    paper: {
        padding: theme.spacing(2),
        boxShadow: '0 2px 5px rgba(0,0,0,0.1)'

},
    form: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',

    },
    formTitle: {
        fontWeight: 'bold'

    },
    fileInput: {
        width: '97%',
        margin: '10px 0',
        overflow: 'hidden',
        textOverflow: 'ellipsis',
    },
    buttonSubmit: {
        marginBottom: 10,
        background: 'black',
        color: 'white'

    },

    /*________________*/

    container: {
        display: 'flex',
        alignItems: 'flex-start',
        justifyContent: 'flex-start',
        width: '100%',
        color: 'red'
    },

/*    input[type="file"] {
        position: absolute;
        z-index: -1;
        top: 10px;
        left: 8px;
        font-size: 17px;
        color: #b8b8b8;
    },

    .button-wrap {
        position: relative;
    },

    .button {
        display: inline-block;
        padding: 12px 18px;
        cursor: pointer;
        border-radius: 5px;
        background-color: #8ebf42;
        font-size: 16px;
        font-weight: bold;
        color: #fff;
    },*/
}));
