import { makeStyles } from '@material-ui/core/styles';

export default makeStyles((theme) => ({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
        },
    },
    paper: {
        padding: theme.spacing(2),
        boxShadow: '0 2px 5px rgba(0,0,0,0.1)'

},
    form: {
        position: 'relative',
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',

    },
    formTitle: {
        fontWeight: 'bold'

    },
    fileInput: {
        width: '97%',
        margin: '10px 0',
        overflow: 'hidden',
        textOverflow: 'ellipsis',
    },
    buttonSubmit: {
        marginBottom: 10,
        background: 'black',
        color: 'white',
        "&:hover": {
            background: "grey"
        },

    },

    /*________________*/

    fileContainer: {
        position: 'relative',
        display: 'flex',
        alignItems: 'flex-start',
        justifyContent: 'flex-start',
        width: '100%',
        overflow: 'hidden',
        textOverflow: 'ellipsis',
        zIndex: '300',
    },

    fileInput: {
        position: 'absolute',
        zIndex: '-1',
        // visibility: 'hidden',
        top: '10px',
        left: '8px',
        fontSize: '17px',
        color: '#b8b8b8',
        marginLeft: 20,
        cursor: 'pointer'
    },

    buttonWrap: {
        position: 'relative',
    },

    button: {
        display: 'inline-block',
        // padding: '12px 18px',
        cursor: 'pointer',
        // borderRadius: '5px',
        backgroundColor: '#3bafda',
        // fontSize: '16px',
        // fontWeight: 'bold',
        // color: '#fff',
        // marginBottom: 10,
        // background: 'black',
        "&:hover": {
            background: "#3bafae"
        },
        color: 'white',
        marginBottom: 10,
    },

    attachFile: {
        position: 'absolute',
        // zIndex: '-1',
        marginLeft: '10px',
        // visibility: 'hidden',
        top: '10px',
        left: '8px',
        fontSize: '17px',
        color: '#b8b8b8',
        marginLeft: 20,
        cursor: 'pointer'
    }
}));
