const [isActive, setActive] = useState("false");

export const handleToggle = () => {
    setActive(!isActive);

    let body = document.body;
    if(isActive){

        body.style.backgroundColor = 'rgba(10,7,14)';
    }else{

        body.style.backgroundColor = 'rgba(252,252,252,255)';
    }
};
