let backToTopEditElement = document.getElementById("backToTopEdit");


export const backToTopEdit = () => {
// scroll back to top
    backToTopEditElement.addEventListener('click', () => {


        window.scrollTo({

            top: 0,
            left: 0,
            behavior: "smooth"
        });

    })

}
